mapper.select:
SELECT id, strategy_id, award_id, award_rate, create_time, update_time
FROM strategy_award
WHERE strategy_id = #{strategyId}

mapper.update:
update strategy_award
set award_count_surplus = award_count_surplus - #{decrAmount}
where strategy_id = #{strategyId} and award_id = #{awardId} and award_count_surplus > 0
